function search_suggest(){if(!network_state){return}if(search_engine==="google"){return 0}var a=$("#search_text").val();var b="http://39.108.75.56/search_suggest?&search_text="+a;$("#search_sug").load(b,function(){$("li.sa_sg").mouseover(function(){$(this).css("background-color","#F5F5F5")}).mouseleave(function(){$(this).css("background-color","#ffffff")}).click(function(){$("#search_text").val($(this).attr("query"));go_search()})})}function go_search(){var a=$("#search_text").val();var b="";if(search_engine==="bing"){b="http://cn.bing.com/search?q="+a+"&go=搜索&qs=bs&form=QBRE&scope=web"}else{b="https://www.google.com/search?q="+a+"&oq="+a}window.open(b);search_reset()}function search_reset(){$("#search_text").val("");$("#search_sug").html("")}function search_engine_switch(a){search_engine=a;if(a==="bing"){$("#engin_title").html('<img src="./img/bing_logo.png" height="40" width="118">　<span class="caret"></span>')}else{if(a==="google"){$("#engin_title").html('<img src="./img/google_logo.png" height="40" width="118">　<span class="caret"></span>')}}};